import React, { useState } from 'react';
import axios from 'axios';
// Make sure to run: npm install axios in the frontend folder

const UploadDoc = () => {
    const [file, setFile] = useState(null);
    const [name, setName] = useState("");
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);
    const [previewUrl, setPreviewUrl] = useState(null);

    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        setFile(selectedFile);

        // Create a local URL to show the image preview immediately
        if (selectedFile) {
            setPreviewUrl(URL.createObjectURL(selectedFile));
        }
    };

    const handleUpload = async () => {
        if (!file || !name) {
            alert("Please enter a name and select a file!");
            return;
        }

        setLoading(true);
        setResult(null); // Clear previous results

        const formData = new FormData();
        formData.append("file", file);
        formData.append("expected_name", name);

        try {
            // Connect to the Python Backend
            const response = await axios.post("http://localhost:8000/verify", formData);
            setResult(response.data);
        } catch (error) {
            console.error("Upload error:", error);
            alert("Failed to verify document. Ensure the Python backend is running on port 8000.");
        } finally {
            setLoading(false);
        }
    };

    // Inline CSS styles for a clean Hackathon UI
    const styles = {
        container: { maxWidth: '700px', margin: '0 auto', padding: '20px', fontFamily: 'Arial, sans-serif' },
        card: { border: '1px solid #e5e7eb', borderRadius: '12px', padding: '25px', boxShadow: '0 4px 6px rgba(0,0,0,0.05)', background: 'white' },
        inputGroup: { marginBottom: '20px' },
        label: { display: 'block', marginBottom: '8px', fontWeight: '600', color: '#374151' },
        input: { width: '100%', padding: '10px', borderRadius: '6px', border: '1px solid #d1d5db', boxSizing: 'border-box' },
        button: { width: '100%', padding: '12px', backgroundColor: '#4f46e5', color: 'white', border: 'none', borderRadius: '6px', cursor: 'pointer', fontSize: '16px', fontWeight: 'bold' },
        imagePreview: { maxWidth: '100%', marginTop: '15px', borderRadius: '8px', border: '2px dashed #e5e7eb' },
        resultCard: { marginTop: '25px', padding: '20px', borderRadius: '12px', border: '1px solid #e5e7eb', background: '#f9fafb' },
        badge: { display: 'inline-block', padding: '6px 12px', borderRadius: '20px', color: 'white', fontWeight: 'bold', marginBottom: '10px', fontSize: '0.9rem' }
    };

    const getRiskColor = (risk) => {
        if (risk === 'LOW') return '#10b981'; // Green
        if (risk === 'MEDIUM') return '#f59e0b'; // Orange
        return '#ef4444'; // Red
    };

    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <h2 style={{marginTop: 0, color: '#1f2937'}}>ðŸ“„ Verify Tenant Document</h2>

                <div style={styles.inputGroup}>
                    <label style={styles.label}>Expected Tenant Name</label>
                    <input
                        type="text"
                        style={styles.input}
                        placeholder="e.g. Rahul Sharma"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </div>

                <div style={styles.inputGroup}>
                    <label style={styles.label}>Upload ID / Rent Agreement</label>
                    <input type="file" onChange={handleFileChange} accept="image/*" style={styles.input} />
                </div>

                {previewUrl && (
                    <img src={previewUrl} alt="Preview" style={styles.imagePreview} />
                )}

                <br /><br />

                <button onClick={handleUpload} disabled={loading} style={{...styles.button, opacity: loading ? 0.7 : 1}}>
                    {loading ? "Running AI Analysis..." : "Verify Document"}
                </button>
            </div>

            {/* RESULT SECTION */}
            {result && (
                <div style={{...styles.resultCard, borderLeft: `6px solid ${getRiskColor(result.risk_level)}`}}>
                    <h3 style={{marginTop: 0}}>Analysis Result</h3>

                    <span style={{...styles.badge, backgroundColor: getRiskColor(result.risk_level)}}>
                {result.risk_level} RISK
            </span>

                    <p style={{fontSize: '1.1rem'}}><strong>Verdict:</strong> {result.message}</p>

                    <div style={{display: 'flex', gap: '20px', marginTop: '15px', background: 'white', padding: '15px', borderRadius: '8px', border: '1px solid #e5e7eb'}}>
                        <div>
                            <small style={{color: '#6b7280'}}>Forgery Probability</small><br/>
                            <strong style={{fontSize: '1.2rem'}}>{result.details.forgery_score}</strong>
                        </div>
                        <div>
                            <small style={{color: '#6b7280'}}>Name Match Score</small><br/>
                            <strong style={{fontSize: '1.2rem'}}>{result.details.name_match_score}</strong>
                        </div>
                    </div>

                    <details style={{marginTop: '15px', fontSize: '0.85em', color: '#6b7280', cursor: 'pointer'}}>
                        <summary>View OCR Extracted Text (Debug)</summary>
                        <p style={{background: '#eee', padding: '10px', borderRadius: '4px'}}>{result.extracted_text_snippet}</p>
                    </details>
                </div>
            )}
        </div>
    );
};

export default UploadDoc;
